sys_variable_value.value:
(function execute(inputs, outputs) {

  var attachments = new GlideRecord("sys_attachment");

  attachments.addQuery("table_sys_id",inputs.sourceRecord.getValue("sys_id"));

  attachments.orderBy("file_name");
  attachments.query();

  var rowCount = attachments.getRowCount();

  outputs.attachmentList = attachments;
  outputs.count = rowCount;

})(inputs, outputs);

