sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	// Get value from the user_name path parameter passed in the URL
	var userName = request.pathParams.user_name;
	// Get value of the ni_query query parameter passed in the URL
	var query = request.queryParams.ni_query;

	// Query the sys_user table to get the user record for the user passed in the user_id path parameter.  
	var user = new GlideRecord('sys_user');
	user.get('user_name', userName);

	// Get the count of active NeedIt table records where Requested for = user
	// The encoded query is from the Query Parameter Associations.  Group by category.
	var aggGR = new GlideAggregate('x_58872_needit_needit');
	aggGR.addAggregate('COUNT');
	aggGR.addQuery('u_requested_for', user.sys_id);
	aggGR.addEncodedQuery(query);
	aggGR.groupBy('u_request_type');
	aggGR.query();

	var typeCount = 0;
	var types = [];
	var totalCount = 0;

	while (aggGR.next()) {
		typeCount = aggGR.getAggregate('COUNT');
		var reqType = aggGR.u_request_type.getDisplayValue();
		types.push({ "requestType": reqType, "count": typeCount });
		totalCount += parseInt(typeCount);
	}

	//Create a body object.  Add property value pairs to the body.
	var body = {};
	body.totalCount = totalCount;
	body.typeCounts = types;
	body.user = { "Username": user.user_name, "UserID": user.sys_id };

	// Send the response object, which is returned to the requestor, to the body object.
	response.setBody(body);

})(request, response);
